import AdminNav from 'components/nav/adminNav';
import HeaderPage from 'components/header/headerPage';
import IdChange from 'views/admin/id/change';
import PerformanceQuery from 'views/admin/performance/query';
import AdmingoodsManage from 'views/admin/goods/goods';
import AdminStationeryManage from 'views/admin/goods/stationery';
export default {
  name: 'AdminHome',
  components: {
    AdminNav,
    IdChange,
    HeaderPage,
    PerformanceQuery,
    AdminGoodsManage,
    AdminStationeryManage
  },

  data() {
    return {
      isShow: '',
      salesmanID: [],
      goods: [],
      stationery: [],
      show1: false,
      show2: false,
      show3: false,
      show4: false,
      show5: false,
      show6: false,
      Values: ['1-1', '2-1', '3-1', '3-2']
    };
  },

  methods: {
    // 从数据库获取数据
    getData() {
      this.$http.get('/api/salesmanID/getID').then(res => {
        this.salesmanID = res.data.data;
      });
      this.$http.get('/api/mall/getGoods').then(res => {
        this.goods = res.data.data;
      });
      this.$http.get('/api/mall/getStationery').then(res => {
        this.stationery = res.data.data;
      });
    },

    updateIsShow(key) {
      this.isShow = key;
    }

  },
  watch: {
    isShow: {
      handler(newValue, oldValue) {
        // 将原页面内容隐藏，新页面内容显示
        if (oldValue === '1-1') {
          this.show1 = false;
        } else if (oldValue === '2-1') {
          this.show2 = false;
        } else if (oldValue === '3-1') {
          this.show3 = false;
        } else if (oldValue === '3-2') {
          this.show4 = false;
        }

        if (newValue === '1-1') {
          this.show1 = true;
        } else if (newValue === '2-1') {
          this.show2 = true;
        } else if (newValue === '3-1') {
          this.show3 = true;
        } else if (newValue === '3-2') {
          this.show4 = true;
        }
      }

    }
  },

  mounted() {
    // 利用vuex获取上一页的index
    this.isShow = this.$store.state.index; // 每次重新渲染都获取一次数据库信息

    this.getData();
  }

};
