import SalesmanNav from 'components/nav/salesmanNav';
import HeaderPage from 'components/header/headerPage';
import BooksManage from 'views/salesman/manage/goods';
import StationeryManage from 'views/salesman/manage/stationery';
import BooksRecord from 'views/salesman/record/goods';
import StationeryRecord from 'views/salesman/record/stationery';
export default {
  name: 'SalesmanHome',
  components: {
    SalesmanNav,
    HeaderPage,
    GoodsManage,
    StationeryManage,
    GoodsRecord,
    StationeryRecord
  },

  data() {
    return {
      isShow: '',
      goods: [],
      stationery: [],
      show1: false,
      show2: false,
      show3: false,
      show4: false,
      Values: ['1-1', '2-1', '3-1', '3-2']
    };
  },

  methods: {
    getData() {
      this.$http.get('/api/mall/getGoods').then(res => {
        this.goods = res.data.data;
      });
      this.$http.get('/api/mall/getStationery').then(res => {
        this.stationery = res.data.data;
      });
    },

    updateIsShow(key) {
      this.isShow = key;
    }

  },
  watch: {
    isShow: {
      handler(newValue, oldValue) {
        // 将原页面内容隐藏，新页面内容显示
        if (oldValue === '1-1') {
          this.show1 = false;
        } else if (oldValue === '1-2') {
          this.show2 = false;
        } else if (oldValue === '2-1') {
          this.show3 = false;
        } else if (oldValue === '2-2') {
          this.show4 = false;
        }

        if (newValue === '1-1') {
          this.show1 = true;
        } else if (newValue === '1-2') {
          this.show2 = true;
        } else if (newValue === '2-1') {
          this.show3 = true;
        } else if (newValue === '2-2') {
          this.show4 = true;
        }
      }

    }
  },

  mounted() {
    this.isShow = this.$store.state.index;
    this.getData();
  }

};
